in;\n    -o-transition: color 0.1s ease-in, background 0.1s ease-in;\n    transition: color 0.1s ease-in, background 0.1s ease-in;\n}\n\na:hover,\na:focus {\n    color: #7b7b7b;\n    text-decoration: none;\n    outline: 0;\n}\n\na:before,\na:after {\n    -webkit-transition: color 0.1s ease-in, background 0.1s ease-in;\n    -moz-transition: color 0.1s ease-in, background 0.1s ease-in;\n    -ms-transition: color 0.1s ease-in, background 0.1s ease-in;\n    -o-transition: color 0.1s ease-in, background 0.1s ease-in;\n    transition: color 0.1s ease-in, background 0.1s ease-in;\n}\n\n hr {\n    display: block;\n    height: 1px;\n    border: 0;\n    border-top: 1px solid #ccc;\n    margin: 1em 0;\n    padding: 0;\n}\n\n\n.navbar {\n\ttext-transform: uppercase;\n\tmargin-bottom: 0px;\n}\n\n.navbar-inverse {\n\tpadding-bottom: 70px;\n\tpadding-top: 70px;\n}\n\n.navbar-inverse {\n\tbackground-color: #1abc9c;\n\tborder-color: #16a085;\n}\n\n.navbar-inverse .navbar-nav > li > a {\n\tcolor: white;\n}\n\n.navbar-inverse .navbar-nav > li > a:hover {\n\tcolor: #222222;\n}\n\n.navbar-brand {\n\tfont-weight: 700;\n\tfont-size: 20px;\n\tletter-spacing: 2px;\n}\n\n.navbar-inverse .navbar-brand {\n\tcolor: white;\n}\n\n.navbar-inverse .navbar-toggle {\n\tborder-color: transparent;\n}\n\n.progress-bar-theme {\n  background-color: #1abc9c;\n}\n\n\n/* Helpers */\n\n.mt {\n\tmargin-top: 40px;\n\tmargin-bottom: 40px;\n}\n\n.pt {\n\tpadding-top: 50px;\n\tpadding-bottom: 50px;\n}\n\n\n/* +++++ WRAP SECTIONS +++++ */\n\n#ww {\n\tpadding-top: 70px;\n\tpadding-bottom: 70px;\n\tbackground-color: #f2f2f2;\n}\n\n\n#footer {\n\tbackground-color: #2f2f2f;\n\tpadding-top: 50px;\n\tpadding-bottom: 50px; \n}\n\n#footer p {\n\tcolor: white;\n}\n\n#footer h4 {\n\tcolor: white;\n\ttext-transform: uppercase;\n\tpadding-bottom: 20px;\n}\n\n\n/* +++++ Color Wraps / Blog Page +++++ */\n\n#grey {\n\tpadding-top: 60px;\n\tpadding-bottom: 60px;\n\tbackground-color: #f2f2f2;\n}\n\n#white {\n\tpadding-top: 60px;\n\tpadding-bottom: 60px;\n\tbackground-color: #ffffff;\n}\n\n\n\n/* Blog Date*/\nbd {\n\tfont-size: 12px;\n\ttext-transform: uppercase;\n\tcolor: #d2d2d2;\n\tfont-weight: 700;\n}\n\n/* Blog Author*/\nba {\n\tfont-size: 12px;\n\ttext-transform: uppercase;\n}\n\n/* Blog Quote */\n.bq {\n\tfont-size: 22px;\n\tpadding-top: 30px;\n}\n\n/* Blog Tags */\nbt {\n\tfont-size: 12px;\n}\n\n/* ================== PORTFOLIO IMAGES HOVER EFFECT ================== */\n/* Effects also are controled by hover.zoom.js */\n.zoom { \n    display:block; \n    position:relative; \n    overflow:hidden; \n    background:transparent url(templates/stanley/images/loader.gif) no-repeat center;\n}\n\n\n    .zoomOverlay {\n        position:absolute;\n        top:0; left:0;\n        bottom:0; right:0;\n        display:none;\n        background-image:url(templates/stanley/images/zoom.png);\n        background-repeat:no-repeat;\n        background-position:center;\n    }\n\n.zoom2 {\n\topacity: 1;\n}\n\n.zoom2:hover {\n\topacity: 0.5;\n}\n\n/* glyphicon Configuration */\n\n.fa {\n\tfont-size: 40px;\n\tcolor: #1abc9c;\n}','(function($){\n\n    $.fn.extend({ \n\n        hoverZoom: function(settings) {\n \n            var defaults = {\n                overlay: true,\n                overlayColor: \'#2e9dbd\',\n                overlayOpacity: 0.9,\n                zoom: 25,\n                speed: 300\n            };\n             \n            var settings = $.extend(defaults, settings);\n         \n            return this.each(function() {\n            \n                var s = settings;\n                var hz = $(this);\n                var image = $(\'img\', hz);\n\n                image.load(function() {\n                    \n                    if(s.overlay === true) {\n                        $(this).parent().append(\'<div class=\"zoomOverlay\" />\');\n                        $(this).parent().find(\'.zoomOverlay\').css({\n                            opacity:0, \n                            display: \'block\', \n                            backgroundColor: s.overlayColor\n                        }); \n                    }\n                \n                    var width = $(this).width();\n                    var height = $(this).height();\n                \n                    $(this).fadeIn(1000, function() {\n                        $(this).parent().css(\'background-image\', \'none\');\n                        hz.hover(function() {\n                            $(\'img\', this).stop().animate({\n                                height: height + s.zoom,\n                                marginLeft: -(s.zoom),\n                                marginTop: -(s.zoom)\n                            }, s.speed);\n                            if(s.overlay === true) {\n                                $(this).parent().find(\'.zoomOverlay\').stop().animate({\n                                    opacity: s.overlayOpacity\n                                }, s.speed);\n                            }\n                        }, function() {\n                            $(\'img\', this).stop().animate({\n                                height: height,\n                                marginLeft: 0,\n                                marginTop: 0\n                            }, s.speed);\n                            if(s.overlay === true) {\n                                $(this).parent().find(\'.zoomOverlay\').stop().animate({\n                                    opacity: 0\n                                }, s.speed);\n                            }\n                        });\n                    });\n                });    \n            });\n        }\n    });\n})(jQuery);\n\n        $(function() {\n        \n        \t\n            $(\'.blue\').hoverZoom({\n                overlayColor: \'#3498db\',\n                zoom: 0\n            });\n            \n            $(\'.green\').hoverZoom({\n                overlayColor: \'#1abc9c\',\n                zoom: 0\n            });